mapper.select:
select if(count(*)=1,1,0)
from user_houseinfo
where member_id=#{id} and houseinfo_aptcode = #{aptCode}

mapper.update:
update user_houseinfo
set flag = !flag
where member_id=#{id} and houseinfo_aptcode = #{aptCode}

update review set residenceType=#{residenceType},
                  residenceYear=#{residenceYear},
                  residenceFloor=#{residenceFloor},
                  content=#{content},
                  stars=#{stars}
where review_id=#{reviewId};
